import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DishItem from './DishItem';
import './App.css';
import noshImage from './components/nosh.png'; // Import the image

function App() {
    const [dishes, setDishes] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/dishes')
            .then(response => setDishes(response.data))
            .catch(error => console.log(error));
    }, []);

    const togglePublished = (id) => {
        const dish = dishes.find(d => d._id === id);
        const updatedDish = { ...dish, isPublished: !dish.isPublished };

        axios.put(`http://localhost:5000/dishes/${id}`, updatedDish)
            .then(response => {
                setDishes(dishes.map(d => d._id === id ? response.data : d));
            })
            .catch(error => console.log(error));
    };

    return (
        <div className="App">
            <header className="App-header">
                <img src={noshImage} alt="Nosh Logo" className="nosh-logo" />
                <h1>Food Menu</h1>
            </header>
            <div className="dish-list">
                {dishes.map(dish => (
                    <DishItem key={dish._id} dish={dish} togglePublished={togglePublished} />
                ))}
            </div>
        </div>
    );
}

export default App;
